import { createSlice } from "@reduxjs/toolkit";
var dispatchArr=[];
var idArr=[];
var slice=createSlice({
    name : 'recepie',
    initialState:[] ,
    reducers:{
        stateChange:(state,action)=>{
            
// alert('l')
            // console.log(JSON.parse(JSON.stringify(JSON.parse(state))));
            var getState=[...state];
            if(getState.length ==0){
                getState.push(action.payload.formjson);
              return  getState 

            }
            else{
                // var temp=[]
                var filterState = getState.map((val) => {
                    if (val.id === action.payload.formjson.id) {
                      // Replace the matching object
                      return action.payload.formjson;
                    }
                    // Keep the existing object
                    return val;
                  });
                 return filterState
