import React,{ useState}  from "react";
import increment from './images/icon-increment-quantity.svg'
import decrement from './images/icon-decrement-quantity.svg'
import Cart from './images/icon-add-to-cart.svg'
import { useSelector,useDispatch } from "react-redux";
import {stateChange} from "./slice"
// import from "react";
var dispatchArray=[];
var DispatchId=[]
  console.log('expot couter')

function Counter(prop){
    console.log('counter runs')
    var evetDispatch=useDispatch()
    console.log(prop.cuurentdata.id)
    var [showCounter,showCounterfn]=useState(false);
    var [add,addfn]=useState(0)
    var setdecrementfn=(e)=>{ 
        console.log(e.target.closest('.counterActive').getAttribute('id'));
        var getId=e.target.closest('.counterActive').getAttribute('name');
        var getname=e.target.closest('.counterActive').getAttribute('id');
        addfn((prev)=>{
            if(prev == 0){
                return 0
            }
            else{
                return (prev- 1)
            }
           
        })
        createJson(getId,getname,'decrement');
     }
    var setAddIncrementfn=(e)=>{
        var getId=e.target.closest('.counterActive').getAttribute('id');
        var getname=e.target.closest('.counterActive').getAttribute('name');
        addfn((prev)=>prev+1);
        createJson(getId,getname,'increment');
    }
        
     function createJson(id,name,type){
        
        var formjson={
            id,name,type
        }
       if(dispatchArray.length == 0){
        dispatchArray.push(formjson)
       }
       else{
        dispatchArray.filter((val)=> {
            return val.id != formjson.id
        })
       }
       console.log('dispatchArray...',dispatchArray)
        
        evetDispatch(stateChange({dispatchArray}))    // in the  Dispatch event we need to pass the params as object

     }
return(
    <>
    <div className="counterParent" onClick={()=>showCounterfn(true)}>
    { showCounter &&  <div className="counterActive" name={prop.cuurentdata.name} id={prop.cuurentdata.id}>
         <span className="addorless" onClick={setdecrementfn} >
            <img src={decrement} alt="" />
            </span>
            <span className="count">{add}</span>
         <span className="addorless" onClick={setAddIncrementfn}>
            <img src={increment} alt="" />
            </span>
        </div>}
            { !showCounter && 
               <div className="flex">
                 <span className="cartImgParnet" >
                   <img src={Cart} alt=''/>
                </span>
                <div className="font-14 addTocartStyles">Add to Cart</div>
               </div>}
    </div>
    
    </>
)

}
export default Counter
