import { createSlice } from "@reduxjs/toolkit";
var dispatchArr=[];
var idArr=[];
var slice=createSlice({
    name : 'recepie',
    initialState:[] ,
    reducers:{
        stateChange:(state,action)=>{
            
// alert('l')
            // console.log(JSON.parse(JSON.stringify(JSON.parse(state))));
            var getState=[...state];
            if(getState.length ==0){
                getState.push(action.payload.formjson);
              return  getState 

            }
            else{
                let filterState = [...getState];
                let itemExists = false;
                getState.forEach((val) => {
                    if (val.id === action.payload.formjson.id) {
                        itemExists = true; // Mark that the item exists
                      }
                });
                if (!itemExists) {
                    filterState.push(action.payload.formjson);
                  }

                  console.log("filterState....", JSON.parse(JSON.stringify(filterState)));
                 return filterState;

            }
            
            // getState.push(filterState);
            
            

            
  
      
    }}
}) 

export var {stateChange}=slice.actions
export var expreducer=slice.reducer
