import { createSlice } from "@reduxjs/toolkit";
var dispatchArr=[];
var idArr=[];
var slice=createSlice({
    name : 'recepie',
    initialState:JSON.parse(sessionStorage.getItem('foodData')) ||[] ,
    reducers:{
        stateChange:(state,action)=>{
            console.log(JSON.parse(JSON.stringify(state)));
            console.log(action.payload.formjson);
            var getAddedItems=state.items.map((items)=>{
               if(items.id == action.payload.formjson.id){
                 items.isAddedtoCart='Y'
               }
               return items
            })
            return {...state,items:getAddedItems}
           
    }}
}) 

export var {stateChange}=slice.actions
export var expreducer=slice.reducer
