import { createSlice } from "@reduxjs/toolkit";
var dispatchArr=[];
var idArr=[];
var slice=createSlice({
    name : 'recepie',
    initialState:[] ,
    reducers:{
        stateChange:(state,action)=>{
            

            // console.log(JSON.parse(JSON.stringify(JSON.parse(state))));
            var getState={...state};
            getState.push(action.payload);
            
            

            
  
       return getState
    }}
}) 

export var {stateChange}=slice.actions
export var expreducer=slice.reducer
