import React, { useState, useEffect } from "react";

function MenuComponent() {
    console.log('Men');
    
    // Declare state for the recipe list
    const [recepieList, recepiefn] = useState([]);

    // Fetch data when component mounts
    useEffect(() => {
        fetch('/recepiedata.json')
            .then((res) => res.json())
            .then((res) => {
                console.log('wres...', res);
                recepiefn(res); // Set the fetched data to state
            })
            .catch((err) => console.log(err));
    }, []);

    // Log the updated recepieList whenever it changes
    useEffect(() => {
        console.log('recepieList updated:', recepieList); // Logs after state changes
    }, [recepieList]); // This effect depends on recepieList

    return (
        <div className="menuparent">
            <span className="font-24">Desserts</span>
            {recepieList.length > 0 &&
                recepieList.map((val, index) => (
                    <div key={index}>{val}</div> // Render each item in recepieList
                ))
            }
        </div>
    );
}

export default MenuComponent;
