import React, { useState, useEffect } from "react";

function MenuComponent() {
    console.log('Men');

    // Declare state for the recipe list
    const [recepieList, recepiefn] = useState([]);

    // Fetch data when component mounts
    useEffect(() => {
        fetch('/recepiedata.json')
            .then((res) => res.json())
            .then((res) => {
                console.log('wres...', res);
                recepiefn(res.items); // Set the 'items' array to state
            })
            .catch((err) => console.log(err));
    }, []); // Empty dependency array means this will only run once after the first render
    return (
        <div className="menuparent">
            <span className="font-24">Desserts</span>
            {recepieList.length > 0 && 
                recepieList.map((val, index) => (
                    <div key={index}>
                        {console.log(val)} {/* Log each item */}
                        <p>{val.name}</p> {/* Render the 'name' of the recipe */}
                        {/* Display the image using the base64 data */}
                        <img src={val.imge_data} alt={val.name} style={{ width: '100px', height: '100px' }} />
                    </div>
                ))
            }
        </div>
    );
}

export default MenuComponent;
