import { createSlice } from "@reduxjs/toolkit";

fetchData()
console.log('slice run first');
async function fetchData() {
   var fetchUrl= await fetch('/recepiedata.json');
   var res=await fetchUrl.json();
   console.log(res);
   sessionStorage.setItem('fooddata',JSON.stringify(res))
}

var getdata =JSON.parse(sessionStorage.getItem('foodData'))
console.log('getdata',getdata);

var loadData=getdata;
var slice=createSlice({
    name : 'recepie',
    initialState:loadData,
    reducers:{
        stateChange:(state,action)=>{

        console.log(action.payload)
    }}
}) 

export var {stateChange}=slice.actions
export var expreducer=slice.reducer
