import React from "react";
import{createPortal} from 'react-dom';
import success from './images/icon-order-confirmed.svg';
import { useSelector } from "react-redux";

function  Confirmation({isopen,ref}){
    var getOrderedItem=useSelector((state)=>state.recepie);
    // if(isopen){
    //     [...document.getElementsByTagName('dialog')].map((dialog)=>dialog.showModal())
        
    // }
    return(
        <>
        <dialog ref={ref} open={isopen} className="result-modal">
           <div> <img src={success} alt="img"/></div>
            <h4>Order Confirmation</h4>
            <span>We hope you enjoyed your food</span>
            {getOrderedItem.map((value)=>{
                return(
                 <li>
                    <span>{value.name}</span>
                    <span>{value.amount}</span>
                 </li>
                )
                
            })}
        </dialog>
        </>
    )
}
export default Confirmation
