import { createSlice } from "@reduxjs/toolkit";
var dispatchArr=[];
var slice=createSlice({
    name : 'recepie',
    initialState:JSON.parse(sessionStorage.getItem('foodData')) ||[] ,
    reducers:{
        stateChange:(state,action)=>{
            // console.log(JSON.parse(sessionStorage.getItem('foodData')))
            dispatchArr.push(action.payload.formjson)
  
        console.log(dispatchArr)
    }}
}) 

export var {stateChange}=slice.actions
export var expreducer=slice.reducer
