import React, { useEffect, useState } from "react";
import noCart from './images/illustration-empty-cart.svg';
import { useSelector } from "react-redux";
var cartArr=[]
 function AddToCart(){
  
   var getListData=useSelector((state)=> state.recepie);
   console.log('getListData...',getListData);
   console.log('addto cart use state runs')
   // var [cartList,listfn]=useState([]);
   
   
   
    return (
        <div className="addtocartParnet">
           { getListData!=undefined && getListData.length==0  ?    (
           <>
            <h3 className="cartCount">Your Cart<span>(0)</span></h3>
            <div className="emptyCart">
             <img src={noCart} alt=''/>
               <div>Your added items will appear here</div>
            </div>
            </>
             ) : (
               <>
               <h3 className="cartCount">Your Cart<span>({getListData.length})</span></h3>
               <div className="ListSection">
               {getListData.map((value)=>{
                  return(
                     <div>
                        <div className="cart_ProductName">{value.name}</div>
                        <span> x{value.count}</span>
                     </div>
                  )

               })}

               </div>
            </>
            )
            
           }
        </div>
    )
 }
 export default AddToCart
