import { createSlice } from "@reduxjs/toolkit";
var dispatchArr=[];
var idArr=[];
var slice=createSlice({
    name : 'recepie',
    initialState:[] ,
    reducers:{
        stateChange:(state,action)=>{
            
// alert('l')
            // console.log(JSON.parse(JSON.stringify(JSON.parse(state))));
            var getState=[...state];
            if(getState.length ==0){
                getState.push(action.payload.formjson);
              return  getState 

            }
            else{
                var filterState=getState.filter((val)=> {
                    console.log(val.id);
                    console.log(action.payload.formjson.id);
                    if(val.id != action.payload.formjson.id){
                        console.log(action.payload.formjson.id)
                    }
                 })
                 return filterState
            }
            
            // getState.push(filterState);
            
            

            
  
      
    }}
}) 

